webhook:SendMultipleEmbeds(embeds)
```

### Automatic Player Join/Leave Notifications

```lua
-- Basic player events
game.Players.PlayerAdded:Connect(function(player)
    pcall(function() -- Always wrap in pcall for production
        webhook:SendJoinMessage(player)
    end)
end)

game.Players.PlayerRemoving:Connect(function(player)
    pcall(function()
        webhook:SendLeaveMessage(player)
    end)
end)
```

### Custom Player Events

```lua
-- Player achievement tracking
local function onPlayerAchievement(player, achievementName, points)
    webhook:SendCustomEvent("üéØ Player Achievement", {
        ["Player"] = player.DisplayName,
        ["Achievement"] = achievementName,
        ["Points Earned"] = tostring(points),
        ["Total Score"] = tostring(leaderstats.Score.Value),
        ["Time"] = os.date("%c")
    })
end

-- Player level up
local function onPlayerLevelUp(player, newLevel, oldLevel)
    webhook:SendEmbed({
        title = "üìà Level Up!",
        description = string.format("**%s** leveled up!", player.DisplayName),
        color = 0xffd700,
        fields = {
            {name = "Previous Level", value = tostring(oldLevel), inline = true},
            {name = "New Level", value = tostring(newLevel), inline = true},
            {name = "Experience", value = "10,000 XP", inline = true}
        },
        thumbnail = {
            url = "https://www.roblox.com/headshot-thumbnail/image?userId=" .. player.UserId .. "&width=420&height=420&format=png"
        }
    })
end
```
